mapper.select:
SELECT 

FROM gen_custom_obj a

WHERE a.id = #{id}

SELECT 

FROM gen_custom_obj a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.label = #{label}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gen_custom_obj a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gen_custom_obj SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
